import pygameimport sysimport randompygame.init()speed = 5width = 800height = 600color = (200,0,0)score=0;player_size = 50player_pos = [width/2, height-2*player_size]player_size = 50screen = pygame.display.set_mode((width , height))game_over = Falsegreen= (0,200,0)enemy_size =50 #the top of the screen start at position 0#here random 0 ,  width- enemy_sizeenemy_pos = [random.randint(0,width-enemy_size ),0]enemy_color = (10,10,200)clock = pygame.time.Clock()enemy_list= []#here we make the list of enemies#if we controll the areas where the enemy will show up we must adjust this functiondef drop_enemies(enemy_list):    delay=random.random()    if len(enemy_list)< 6 and delay<0.1:       x_pos  = random.randint(0,width- enemy_size)       y_pos = 0       enemy_list.append([x_pos, y_pos])def draw_enemies(enemy_list):   for enemy_pos in enemy_list:      pygame.draw.rect(screen,enemy_color,(enemy_pos[0],enemy_pos[1],enemy_size,enemy_size))#this code update the position of the enemy, make the enemy movesdef update_enemy_pos(enemy_list,score):   for index, enemy_pos in enumerate(enemy_list):         if enemy_pos[1] >= 0 and enemy_pos[1]< height:           enemy_pos[1] += speed         else:            enemy_list.pop(index)            score+=1   return score      #in this line we decide whether  player has lost or not.def detect_collision(player_pos, enemy_pos):   p_x = player_pos[0]   p_y = player_pos[1]   e_x = enemy_pos[0]   e_y = enemy_pos[1]   if (p_x > e_x and p_x < e_x + enemy_size ) or(e_x >p_x and e_x < (p_x + player_size)):         if (p_y > e_y and p_y< e_y+enemy_size) or (e_y> p_y and e_y< p_y+player_size):            return True   return False#checks if the player t an enemy.def collision(enemy_list,player_pos):   for enemy_pos in enemy_list:      if detect_collision(player_pos,enemy_pos) == True :         return True   else :       return Falsewhile not game_over: for event in pygame.event.get():            if event.type == pygame.QUIT:              pygame.quit()              exit()            if event.type == pygame.KEYDOWN:               x = player_pos[0]               y = player_pos[1]                             if event.key == pygame.K_LEFT:                x -= player_size               elif event.key == pygame.K_RIGHT:                  x += player_size                              elif event.key == pygame.K_UP:                y-= player_size               elif event.key == pygame.K_DOWN:                  y+= player_size               player_pos = [x,y]              screen.fill(green) drop_enemies(enemy_list) draw_enemies(enemy_list) score=update_enemy_pos(enemy_list,score) print(score) if collision(enemy_list,player_pos)== True :   game_over = True   break         pygame.draw.rect(screen,color,( player_pos[0],player_pos[1], player_size, player_size))   clock.tick(30) pygame.display.update()if game_over==True :   pygame.quit()   